CREATE TABLE IF NOT EXISTS 'movies_info_1' ('show_id' INTEGER, 'genre' TEXT, 'title' TEXT, 'director' TEXT, PRIMARY KEY('show_id'));
CREATE TABLE IF NOT EXISTS 'movies_info_2' ('show_id' INTEGER, 'release_year' TEXT, 'description' TEXT, PRIMARY KEY ('show_id'), FOREIGN KEY('show_id') REFERENCES 'movies_info_1' ('show_id'));
SELECT m1.genre AS Genre, m1.title AS Title, m1.director AS Director, m2.release_year AS Year, m2.description AS Description FROM movies_info_1 AS m1 JOIN movies_info_2 AS m2 ON m1.show_id = m2.show_id;